// Get configuration from environment variables with defaults
return [{
  json: {
    project_name: 'Pets Gallery',
    cat_api_url: $env.PETS_CAT_API_URL || 'https://api.thecatapi.com/v1/images/search',
    dog_api_url: $env.PETS_DOG_API_URL || 'https://api.thedogapi.com/v1/images/search',
    cat_breed_url: $env.PETS_CAT_BREED_URL || 'https://api.thecatapi.com/v1/breeds',
    dog_breed_url: $env.PETS_DOG_BREED_URL || 'https://api.thedogapi.com/v1/breeds',
    gallery_title: $env.PETS_GALLERY_TITLE || 'üêæ Pet Gallery üêæ',
    webhook_path: $env.PETS_WEBHOOK_PATH || 'pets',
    images_per_type: parseInt($env.PETS_IMAGES_PER_TYPE) || 5,
    cache_duration_hours: parseInt($env.PETS_CACHE_DURATION_HOURS) || 6,
    max_retries: 3,
    include_breeds: $env.PETS_INCLUDE_BREEDS === 'true' || ($env.PETS_INCLUDE_BREEDS === undefined ? true : false),
    enable_caching: true,
    send_notifications: false
  }
}];
